import React from 'react';
import { increment, decrement, reset } from '../../store/actions/counterActions';
import { useSelector, useDispatch } from 'react-redux';
import JWT from '../../jwt-parser/jwt-parser.js';

function Counter() {
  const counter = useSelector(state => state);
  const dispatch = useDispatch();
  return (
    <div className = "App">
      <div>{counter}</div>
        <button onClick = {()=>{dispatch(increment());}}>INCREMENT BY 1</button>
        <button onClick = {()=>{dispatch(decrement());}}>DECREMENT BY 1</button>
        <button onClick = {()=>{dispatch(reset());}}>RESET</button>
        <button onClick = {()=>{JWT.parse("eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..kJsZuRrqspFXaABSO3jOVzB4RKdMIpyhisp7CO2se8c");}}>
          parse jwt
        </button>
    </div>
  );
}

export default Counter;